import React, { useState } from "react";

const CheckoutForm = ({ processPayment }) => {
  const [formData, setFormData] = useState({
    cardName: "",
    cardNumber: "",
    cardExpiry: "",
    cardCvv: ""
  });

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  return (
    <div className="bg-gray-800 p-6 rounded-xl space-y-4">
      <h2 className="text-white font-bold text-xl">Checkout</h2>
      {["cardName", "cardNumber", "cardExpiry", "cardCvv"].map((field) => (
        <div key={field}>
          <label className="block text-gray-300 mb-1 capitalize">
            {field.replace("card", "").replace(/([A-Z])/g, " $1")}
          </label>
          <input
            id={field}
            value={formData[field]}
            onChange={handleChange}
            placeholder={
              field === "cardNumber"
                ? "1234 5678 9012 3456"
                : field === "cardExpiry"
                ? "MM/YY"
                : field === "cardCvv"
                ? "123"
                : "Cardholder Name"
            }
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-2 focus:ring-indigo-500"
          />
        </div>
      ))}
      <button
        onClick={() => processPayment(formData)}
        className="w-full py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg"
      >
        Pay Now
      </button>
    </div>
  );
};

export default CheckoutForm;
