import React from "react";
import { formatPrice } from "../utils/formatPrice";

const ProductCard = ({ product, addToCart }) => (
  <div className="bg-gray-800 rounded-xl shadow-md hover:shadow-xl overflow-hidden transition-transform hover:-translate-y-1">
    <img
      src={product.image}
      alt={product.name}
      className="h-48 w-full object-cover"
    />
    <div className="p-4">
      <h3 className="text-lg font-semibold text-white">{product.name}</h3>
      <p className="text-indigo-400 font-bold">{formatPrice(product.price)}</p>
      <button
        className="mt-3 w-full bg-indigo-600 hover:bg-indigo-700 text-white py-2 px-4 rounded-lg"
        onClick={() => addToCart(product.id)}
      >
        Add to Cart
      </button>
    </div>
  </div>
);

export default ProductCard;
