import { useState, useEffect } from "react";

const products = [
  { id: 1, name: "iPhone 15 Pro", price: 119900, image: "/public/product/Iphone 15 Pro.png" },
  { id: 2, name: "MacBook Air M3", price: 114900, image: "/public/product/MacBook Air M3.png" },
  { id: 3, name: "Sony WH-1000XM5", price: 29990, image: "/public/product/Sony WH-1000XM5.png" },
  { id: 4, name: "Samsung Galaxy Watch 6", price: 24999, image: "/public/product/Samsung Galaxy Watch 6.png" },
  { id: 5, name: "iPad Air", price: 59900, image: "/public/product/Ipad Air.png" },
  { id: 6, name: "Canon EOS R8", price: 189900, image: "/public/product/Canon EOS R8.png" },
  { id: 7, name: "OnePlus 12", price: 64999, image: "/public/product/OnePlus.png" },
  { id: 8, name: "Dell XPS 13", price: 109990, image: "/public/product/Dell XPS.png" }
];

const formatPrice = (price) =>
  new Intl.NumberFormat("en-IN", {
    style: "currency",
    currency: "INR",
    maximumFractionDigits: 0
  }).format(price);

const ProductCard = ({ product, addToCart }) => (
  <div className="bg-gray-800 rounded-xl shadow-md hover:shadow-xl overflow-hidden transition-transform hover:-translate-y-1">
    <img
      src={product.image}
      alt={product.name}
      className="h-48 w-full object-cover"
    />
    <div className="p-4">
      <h3 className="text-lg font-semibold text-white">{product.name}</h3>
      <p className="text-indigo-400 font-bold">{formatPrice(product.price)}</p>
      <button
        className="mt-3 w-full bg-indigo-600 hover:bg-indigo-700 text-white py-2 px-4 rounded-lg"
        onClick={() => addToCart(product.id)}
      >
        Add to Cart
      </button>
    </div>
  </div>
);

const CartItem = ({ item, increaseQuantity, decreaseQuantity }) => {
  const itemTotal = item.price * item.quantity;
  return (
    <div className="flex justify-between items-center py-2 border-b border-gray-700 text-white">
      <div className="flex-1">{item.name}</div>
      <div className="flex items-center space-x-2">
        <button
          onClick={() => decreaseQuantity(item.id)}
          className="bg-gray-600 px-2 rounded hover:bg-red-500"
        >
          -
        </button>
        <span>{item.quantity}</span>
        <button
          onClick={() => increaseQuantity(item.id)}
          className="bg-gray-600 px-2 rounded hover:bg-green-500"
        >
          +
        </button>
      </div>
      <span>{formatPrice(itemTotal)}</span>
    </div>
  );
};

const CheckoutForm = ({ processPayment }) => {
  const [formData, setFormData] = useState({
    cardName: "",
    cardNumber: "",
    cardExpiry: "",
    cardCvv: ""
  });

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  return (
    <div className="bg-gray-800 p-6 rounded-xl space-y-4">
      <h2 className="text-white font-bold text-xl">Checkout</h2>
      {["cardName", "cardNumber", "cardExpiry", "cardCvv"].map((field) => (
        <div key={field}>
          <label className="block text-gray-300 mb-1 capitalize">
            {field.replace("card", "").replace(/([A-Z])/g, " $1")}
          </label>
          <input
            id={field}
            value={formData[field]}
            onChange={handleChange}
            placeholder={
              field === "cardNumber"
                ? "1234 5678 9012 3456"
                : field === "cardExpiry"
                ? "MM/YY"
                : field === "cardCvv"
                ? "123"
                : "Cardholder Name"
            }
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-2 focus:ring-indigo-500"
          />
        </div>
      ))}
      <button
        onClick={() => processPayment(formData)}
        className="w-full py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg"
      >
        Pay Now
      </button>
    </div>
  );
};

export default function App() {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    const totalAmount = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
    setTotal(totalAmount);
  }, [cart]);

  const addToCart = (productId) => {
    const product = products.find((p) => p.id === productId);
    if (product) {
      setCart((prev) => {
        const exists = prev.find((item) => item.id === productId);
        return exists
          ? prev.map((item) =>
              item.id === productId
                ? { ...item, quantity: item.quantity + 1 }
                : item
            )
          : [...prev, { ...product, quantity: 1 }];
      });
    }
  };

  const decreaseQuantity = (id) =>
    setCart((prev) =>
      prev
        .map((item) =>
          item.id === id ? { ...item, quantity: item.quantity - 1 } : item
        )
        .filter((item) => item.quantity > 0)
    );

  const increaseQuantity = (id) =>
    setCart((prev) =>
      prev.map((item) =>
        item.id === id ? { ...item, quantity: item.quantity + 1 } : item
      )
    );

  const isFraudulentCard = (cardNumber) => cardNumber.endsWith("0000");

  const processPayment = (formData) => {
    if (!formData.cardNumber || cart.length === 0) {
      alert("❌ Invalid card or empty cart");
      return;
    }

    if (isFraudulentCard(formData.cardNumber)) {
      alert("🚨 Fraud detected! Payment blocked.");
    } else {
      alert(`✅ Payment Successful: ${formatPrice(total)}`);
      setCart([]);
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <h1 className="text-4xl font-bold mb-8 text-center">🛒 Naan Mudhalavan Store</h1>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Product Section */}
        <div className="lg:col-span-2 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {products.map((product) => (
            <ProductCard key={product.id} product={product} addToCart={addToCart} />
          ))}
        </div>

        {/* Cart + Checkout */}
        <div className="space-y-6">
          <div className="bg-gray-800 p-4 rounded-xl max-h-96 overflow-y-auto shadow-lg">
            <h2 className="text-2xl font-semibold mb-4">Cart</h2>
            {cart.length === 0 ? (
              <p className="text-gray-400">Your cart is empty.</p>
            ) : (
              cart.map((item) => (
                <CartItem
                  key={item.id}
                  item={item}
                  increaseQuantity={increaseQuantity}
                  decreaseQuantity={decreaseQuantity}
                />
              ))
            )}
            {cart.length > 0 && (
              <div className="mt-4 font-bold text-indigo-300">
                Total: {formatPrice(total)}
              </div>
            )}
          </div>

          <CheckoutForm processPayment={processPayment} />
        </div>
      </div>
    </div>
  );
}
