import React from "react";
import { formatPrice } from "../utils/formatPrice";

const CartItem = ({ item, increaseQuantity, decreaseQuantity }) => {
  const itemTotal = item.price * item.quantity;
  return (
    <div className="flex justify-between items-center py-2 border-b border-gray-700 text-white">
      <div className="flex-1">{item.name}</div>
      <div className="flex items-center space-x-2">
        <button
          onClick={() => decreaseQuantity(item.id)}
          className="bg-gray-600 px-2 rounded hover:bg-red-500"
        >
          -
        </button>
        <span>{item.quantity}</span>
        <button
          onClick={() => increaseQuantity(item.id)}
          className="bg-gray-600 px-2 rounded hover:bg-green-500"
        >
          +
        </button>
      </div>
      <span>{formatPrice(itemTotal)}</span>
    </div>
  );
};

export default CartItem;
